mapper.update:
update role_account_resource v
set v.web_id = #{web_id}
         , v.accountId
            = #{accountId}
         , v.username
            = #{username}
         , v.password
            = #{password}
         , v.roleId
            = #{roleId}
         , v.roleName
            = #{roleName}
         , v.resourceId
            = #{resourceId}
         , v.resourceName
            = #{resourceName}
         , v.apiUrl
            = #{apiUrl}
where v.web_id = #{web_id}
